viewof theme = {
  const key = "theme"
  const stored = localStorage.getItem(key)
  const initial = stored ?? "light"

  const input = Inputs.select(["light", "dark"], {
    label: "Theme",
    value: initial
  })

  // Sync to localStorage on change
  input.addEventListener("input", () =&gt; {
    localStorage.setItem(key, input.value)
  })

  return input
}







